/* App-specific styles */
.App {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

.App main {
  flex: 1;
}

/* Loading spinner */
.spinner {
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 2s linear infinite;
  margin: 0 auto;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: #888;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #555;
}

/* Responsive utilities */
@media (max-width: 768px) {
  .container {
    padding: 0 0.5rem;
  }

  .grid-cols-3 {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (max-width: 480px) {
  .grid-cols-3,
  .md\:grid-cols-3 {
    grid-template-columns: 1fr;
  }
}

/* Animation classes */
.fade-in {
  animation: fadeIn 0.3s ease-in;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.slide-up {
  animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* ===== ADMIN SIDEBAR LAYOUT ===== */
.admin-layout {
  min-height: calc(100vh - 100px);
}

.admin-sidebar {
  width: 280px;
  min-width: 280px;
  box-shadow: 2px 0 4px rgba(0,0,0,0.1);
}

.admin-sidebar .nav-link {
  border-radius: 8px;
  border: none;
  color: #6c757d;
  font-weight: 500;
  padding: 12px 16px;
  transition: all 0.3s ease;
}

.admin-sidebar .nav-link:hover {
  background-color: #f8f9fa;
  color: #dc3545;
  transform: translateX(4px);
}

.admin-sidebar .nav-link.active {
  background-color: #dc3545;
  color: white;
  box-shadow: 0 2px 8px rgba(220, 53, 69, 0.3);
}

.admin-content {
  background-color: #f8f9fa;
  overflow-y: auto;
  padding: 1.5rem;
}

/* ===== ENHANCED ADMIN LAYOUT ===== */
.admin-layout {
  display: flex;
  min-height: calc(100vh - 100px);
  background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
}

.admin-sidebar {
  width: 280px;
  min-width: 280px;
  background: white;
  box-shadow: 2px 0 20px rgba(0,0,0,0.08);
  display: flex;
  flex-direction: column;
  position: relative;
  z-index: 100;
}

.sidebar-header {
  padding: 1.5rem;
  border-bottom: 1px solid #e2e8f0;
  background: linear-gradient(135deg, #ffffff 0%, #f8fafc 100%);
}

.sidebar-brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.sidebar-brand i {
  color: #dc3545;
  font-size: 1.5rem;
}

.brand-text {
  font-size: 1.25rem;
  font-weight: 700;
  color: #1e293b;
}

.sidebar-nav {
  flex: 1;
  padding: 1rem;
}

.nav-item {
  display: flex;
  align-items: center;
  gap: 1rem;
  width: 100%;
  padding: 0.875rem 1rem;
  margin-bottom: 0.5rem;
  border: none;
  background: none;
  border-radius: 12px;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  font-weight: 500;
  color: #64748b;
  text-align: left;
}

.nav-item:hover {
  background: rgba(59, 130, 246, 0.1);
  color: #3b82f6;
  transform: translateX(4px);
}

.nav-item.active {
  background: linear-gradient(135deg, #dc3545, #b91c1c);
  color: white;
  box-shadow: 0 4px 20px rgba(220, 53, 69, 0.3);
  transform: translateX(8px);
}

.nav-icon {
  width: 24px;
  text-align: center;
  font-size: 1.1rem;
}

.nav-label {
  font-size: 0.9375rem;
  font-weight: 600;
}

.content-header {
  display: flex;
  align-items: center;
  justify-content: between;
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
}

.page-title {
  font-size: 1.875rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.content-body {
  flex: 1;
}

/* ===== MODERN KPI CARDS ===== */
.kpi-grid {
  margin-bottom: 2rem;
}

.kpi-card {
  background: white;
  border: 1px solid #f1f5f9;
  border-radius: 16px;
  padding: 1.5rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  transition: all 0.3s ease;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
  min-width: 200px;
}

.kpi-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.12);
  border-color: #e2e8f0;
}

.kpi-icon {
  width: 60px;
  height: 60px;
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.5rem;
  flex-shrink: 0;
}

.kpi-content {
  flex: 1;
}

.kpi-value {
  font-size: 1.875rem;
  font-weight: 700;
  line-height: 1.2;
  margin-bottom: 0.25rem;
}

.kpi-label {
  font-size: 0.875rem;
  color: #64748b;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* ===== ADMIN PANELS ===== */
.admin-panel {
  background: white;
  border: 1px solid #f1f5f9;
  border-radius: 20px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  overflow: hidden;
  transition: all 0.3s ease;
}

.admin-panel:hover {
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.12);
  transform: translateY(-2px);
}

.panel-header {
  background: linear-gradient(135deg, #ffffff 0%, #f8fafc 100%);
  padding: 1.5rem;
  border-bottom: 1px solid #f1f5f9;
}

.panel-title {
  font-size: 1.25rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.panel-body {
  padding: 1.5rem;
}

/* ===== STAFF CARDS ===== */
.staff-grid {
  gap: 1rem;
}

.staff-card {
  background: #f8fafc;
  border: 1px solid #e2e8f0;
  border-radius: 12px;
  padding: 1rem;
  display: flex;
  align-items: center;
  gap: 0.75rem;
  transition: all 0.2s ease;
  min-width: 200px;
}

.staff-card:hover {
  background: white;
  border-color: #3b82f6;
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(59, 130, 246, 0.15);
}

.staff-avatar {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #3b82f6, #1d4ed8);
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.1rem;
  flex-shrink: 0;
}

.staff-avatar.large {
  width: 48px;
  height: 48px;
  font-size: 1.25rem;
}

.staff-info {
  flex: 1;
}

.staff-name {
  font-size: 0.9375rem;
  font-weight: 600;
  color: #1e293b;
  margin-bottom: 0.125rem;
}

.staff-role {
  font-size: 0.75rem;
  color: #64748b;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.staff-stats {
  text-align: right;
  font-size: 0.8125rem;
}

.stat-orders {
  font-weight: 600;
  color: #1e293b;
}

.stat-sales {
  font-weight: 600;
}

/* ===== STAFF MANAGEMENT CARDS ===== */
.staff-management-grid {
  gap: 1.25rem;
}

.staff-management-card {
  background: white;
  border: 1px solid #f1f5f9;
  border-radius: 16px;
  padding: 1.25rem;
  transition: all 0.3s ease;
  min-width: 240px;
  position: relative;
}

.staff-management-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.12);
  border-color: #e2e8f0;
}

.staff-card-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 1rem;
}

.staff-badge {
  position: absolute;
  top: 1rem;
  right: 1rem;
}

.staff-card-body {
  margin-bottom: 1rem;
}

.staff-performance {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  margin-top: 0.75rem;
}

.performance-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 0.8125rem;
}

.performance-label {
  color: #64748b;
  font-weight: 500;
}

.performance-value {
  font-weight: 600;
  color: #1e293b;
}

.staff-card-actions {
  display: flex;
  gap: 0.5rem;
  justify-content: flex-end;
}

/* ===== STOCK ALERTS ===== */
.no-alerts {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  padding: 2rem;
  text-align: center;
  color: #10b981;
  font-weight: 600;
  background: #f0fdf4;
  border-radius: 12px;
  border: 2px dashed #bbf7d0;
}

.no-alerts i {
  font-size: 1.5rem;
  color: #10b981;
}

.stock-alerts {
  max-height: 300px;
  overflow-y: auto;
}

.stock-alert {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.875rem 1rem;
  border-radius: 12px;
  transition: all 0.2s ease;
}

.stock-alert.warning {
  background: #fffbeb;
  border: 1px solid #fde68a;
}

.stock-alert.critical {
  background: #fef2f2;
  border: 1px solid #fecaca;
}

.stock-alert:hover {
  transform: translateX(4px);
}

.alert-icon {
  width: 32px;
  height: 32px;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  flex-shrink: 0;
}

.stock-alert.warning .alert-icon {
  background: #fbbf24;
  color: white;
}

.stock-alert.critical .alert-icon {
  background: #ef4444;
  color: white;
}

.alert-content {
  flex: 1;
}

.alert-title {
  font-weight: 600;
  color: #1e293b;
  margin-bottom: 0.125rem;
}

.alert-message {
  font-size: 0.8125rem;
  color: #64748b;
}

/* ===== FORM STYLING ===== */
.add-staff-form .form-group {
  margin-bottom: 1.25rem;
}

.form-label {
  display: block;
  font-size: 0.875rem;
  font-weight: 600;
  color: #374151;
  margin-bottom: 0.5rem;
}

.form-control {
  border-radius: 8px;
  border: 1px solid #d1d5db;
  transition: all 0.2s ease;
}

.form-control:focus {
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
}

/* ===== MOBILE ADMIN LAYOUT ===== */
.admin-layout.mobile {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  background: #f8fafc;
}

.mobile-hidden {
  display: none !important;
}

/* ===== MOBILE HEADER ===== */
.mobile-header {
  position: sticky;
  top: 0;
  z-index: 200;
  background: white;
  border-bottom: 1px solid #e2e8f0;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.mobile-header-content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem 1.25rem;
  max-width: 100%;
}

.mobile-menu-toggle {
  width: 44px;
  height: 44px;
  border: none;
  background: #f1f5f9;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #475569;
  font-size: 1.25rem;
  transition: all 0.2s ease;
  cursor: pointer;
  touch-action: manipulation;
}

.mobile-menu-toggle:hover,
.mobile-menu-toggle:active {
  background: #e2e8f0;
  color: #dc3545;
  transform: scale(1.05);
}

.mobile-page-title {
  font-size: 1.25rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0;
  text-align: center;
  flex: 1;
  padding: 0 1rem;
}

.mobile-user-info {
  display: flex;
  align-items: center;
}

.mobile-user-avatar {
  width: 44px;
  height: 44px;
  background: linear-gradient(135deg, #dc3545, #b91c1c);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.1rem;
}

/* ===== MOBILE MENU OVERLAY ===== */
.mobile-menu-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  z-index: 300;
  animation: fadeIn 0.2s ease;
}

.mobile-menu {
  position: absolute;
  top: 0;
  left: 0;
  width: 320px;
  max-width: 90vw;
  height: 100vh;
  background: white;
  box-shadow: 4px 0 20px rgba(0, 0, 0, 0.15);
  animation: slideInLeft 0.3s ease;
  overflow-y: auto;
}

.mobile-menu-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.5rem;
  border-bottom: 1px solid #e2e8f0;
  background: linear-gradient(135deg, #dc3545, #b91c1c);
  color: white;
}

.mobile-brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  font-size: 1.25rem;
  font-weight: 700;
}

.mobile-brand i {
  font-size: 1.5rem;
}

.mobile-menu-close {
  width: 40px;
  height: 40px;
  border: none;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  color: white;
  font-size: 1.1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s ease;
}

.mobile-menu-close:hover {
  background: rgba(255, 255, 255, 0.2);
}

.mobile-nav {
  padding: 1rem 0;
}

.mobile-nav-item {
  display: flex;
  align-items: center;
  gap: 1rem;
  width: 100%;
  padding: 1rem 1.5rem;
  border: none;
  background: none;
  color: #475569;
  font-size: 1rem;
  font-weight: 500;
  text-align: left;
  transition: all 0.2s ease;
  cursor: pointer;
  touch-action: manipulation;
  min-height: 60px;
}

.mobile-nav-item:hover {
  background: #f8fafc;
  color: #dc3545;
}

.mobile-nav-item.active {
  background: rgba(220, 53, 69, 0.1);
  color: #dc3545;
  border-right: 4px solid #dc3545;
}

.mobile-nav-icon {
  width: 24px;
  text-align: center;
  font-size: 1.25rem;
  flex-shrink: 0;
}

.mobile-nav-label {
  flex: 1;
  font-weight: 600;
}

.mobile-nav-arrow {
  font-size: 0.875rem;
  color: #94a3b8;
  transition: transform 0.2s ease;
}

.mobile-nav-item:hover .mobile-nav-arrow {
  transform: translateX(4px);
}

/* ===== MOBILE BOTTOM NAVIGATION ===== */
.mobile-bottom-nav {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  background: white;
  border-top: 1px solid #e2e8f0;
  box-shadow: 0 -4px 20px rgba(0, 0, 0, 0.1);
  display: flex;
  padding: 0.5rem;
  z-index: 150;
  safe-area-inset-bottom: env(safe-area-inset-bottom);
}

.bottom-nav-item {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 0.5rem;
  border: none;
  background: none;
  border-radius: 12px;
  transition: all 0.2s ease;
  cursor: pointer;
  touch-action: manipulation;
  min-height: 60px;
  color: #64748b;
}

.bottom-nav-item:hover {
  background: rgba(59, 130, 246, 0.1);
  color: #3b82f6;
}

.bottom-nav-item.active {
  background: rgba(220, 53, 69, 0.1);
  color: #dc3545;
}

.bottom-nav-icon {
  font-size: 1.25rem;
  margin-bottom: 0.25rem;
  transition: transform 0.2s ease;
}

.bottom-nav-item:active .bottom-nav-icon {
  transform: scale(0.95);
}

.bottom-nav-label {
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  line-height: 1;
}

/* ===== MOBILE CONTENT ADJUSTMENTS ===== */
.admin-layout.mobile .admin-content {
  flex: 1;
  padding: 1rem;
  margin-bottom: 80px; /* Space for bottom nav */
  overflow-y: auto;
}

.admin-layout.mobile .content-body {
  padding: 0;
}

/* ===== MOBILE PANEL OPTIMIZATIONS ===== */
.admin-layout.mobile .admin-panel {
  margin-bottom: 1rem;
  border-radius: 16px;
}

.admin-layout.mobile .panel-header {
  padding: 1.25rem;
}

.admin-layout.mobile .panel-body {
  padding: 1.25rem;
}

.admin-layout.mobile .panel-title {
  font-size: 1.125rem;
}

/* ===== MOBILE KPI CARDS ===== */
.admin-layout.mobile .kpi-grid {
  flex-direction: column;
  gap: 0.75rem;
  margin-bottom: 1.5rem;
}

.admin-layout.mobile .kpi-card {
  min-width: auto;
  padding: 1.25rem;
  border-radius: 16px;
}

.admin-layout.mobile .kpi-icon {
  width: 48px;
  height: 48px;
  font-size: 1.25rem;
}

.admin-layout.mobile .kpi-value {
  font-size: 1.5rem;
}

/* ===== MOBILE STAFF CARDS ===== */
.admin-layout.mobile .staff-grid,
.admin-layout.mobile .staff-management-grid {
  flex-direction: column;
  gap: 0.75rem;
}

.admin-layout.mobile .staff-card,
.admin-layout.mobile .staff-management-card {
  min-width: auto;
}

/* ===== MOBILE FORM IMPROVEMENTS ===== */
.admin-layout.mobile .form-control {
  font-size: 16px; /* Prevents zoom on iOS */
  padding: 0.875rem;
  border-radius: 12px;
}

.admin-layout.mobile .btn {
  min-height: 44px;
  font-size: 1rem;
  border-radius: 12px;
  font-weight: 600;
}

/* ===== MOBILE ANIMATIONS ===== */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideInLeft {
  from { transform: translateX(-100%); }
  to { transform: translateX(0); }
}

/* ===== RESPONSIVE ADMIN LAYOUT (UPDATED) ===== */
@media (max-width: 1200px) {
  .admin-grid-layout {
    flex-direction: column;
  }

  .kpi-grid {
    flex-direction: column;
    gap: 1rem;
  }

  .kpi-card {
    min-width: auto;
  }
}

@media (max-width: 992px) {
  .admin-sidebar:not(.mobile-hidden) {
    position: fixed;
    left: -280px;
    top: 0;
    height: 100vh;
    z-index: 1000;
    transition: left 0.3s ease;
  }

  .admin-sidebar.open {
    left: 0;
  }

  .admin-content {
    width: 100%;
    padding: 1rem;
  }

  .staff-management-grid {
    flex-direction: column;
  }

  .staff-management-card {
    min-width: auto;
  }
}

@media (max-width: 768px) {
  .kpi-grid {
    gap: 0.75rem;
  }

  .kpi-card {
    flex-direction: column;
    text-align: center;
    padding: 1.25rem;
  }

  .kpi-icon {
    width: 48px;
    height: 48px;
    font-size: 1.25rem;
  }

  .staff-grid {
    flex-direction: column;
  }

  .staff-card {
    min-width: auto;
  }

  .page-title {
    font-size: 1.5rem;
  }

  .panel-title {
    font-size: 1.125rem;
  }
}

@media (max-width: 480px) {
  .admin-content {
    padding: 0.75rem;
  }

  .kpi-value {
    font-size: 1.5rem;
  }

  .kpi-label {
    font-size: 0.8125rem;
  }

  .panel-header,
  .panel-body {
    padding: 1rem;
  }

  .mobile-page-title {
    font-size: 1.125rem;
  }

  .mobile-menu {
    width: 100%;
    max-width: 100vw;
  }

  .bottom-nav-label {
    font-size: 0.6875rem;
  }
}

/* ===== PWA STAFF LAYOUT ===== */
.pwa-staff-layout {
  min-height: calc(100vh - 80px);
  background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
  display: flex;
  flex-direction: column;
}

/* PWA Navigation Tabs */
.pwa-nav-tabs {
  background: white;
  border-bottom: 1px solid #e2e8f0;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
  position: sticky;
  top: 0;
  z-index: 100;
}

.pwa-nav-container {
  display: flex;
  justify-content: center;
  padding: 0.5rem 1rem;
  max-width: 1200px;
  margin: 0 auto;
  gap: 0.5rem;
}

.pwa-nav-tab {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 0.75rem 1rem;
  border: none;
  background: none;
  border-radius: 12px;
  transition: all 0.2s ease;
  cursor: pointer;
  min-width: 80px;
  color: #64748b;
  font-weight: 500;
}

.pwa-nav-tab:hover {
  background: rgba(59, 130, 246, 0.1);
  color: #3b82f6;
  transform: translateY(-2px);
}

.pwa-nav-tab.active {
  background: linear-gradient(135deg, #3b82f6, #1d4ed8);
  color: white;
  box-shadow: 0 4px 12px rgba(59, 130, 246, 0.3);
}

.pwa-tab-icon {
  font-size: 1.25rem;
  margin-bottom: 0.25rem;
}

.pwa-tab-label {
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* PWA Main Content */
.pwa-main-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  max-width: 1400px;
  margin: 0 auto;
  width: 100%;
  padding: 0 1rem;
}

.pwa-main-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  margin-bottom: 1.5rem;
}

.pwa-header-info {
  flex: 1;
}

.pwa-main-title {
  font-size: 1.875rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0 0 0.25rem 0;
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.pwa-main-title i {
  color: #3b82f6;
}

.pwa-main-subtitle {
  color: #64748b;
  margin: 0;
  font-size: 0.875rem;
}

.pwa-header-user {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  background: white;
  padding: 0.75rem 1rem;
  border-radius: 16px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.pwa-user-avatar {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #3b82f6, #1d4ed8);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.1rem;
}

.pwa-user-info {
  display: flex;
  flex-direction: column;
}

.pwa-user-name {
  font-weight: 600;
  color: #1e293b;
  font-size: 0.875rem;
}

.pwa-user-role {
  color: #64748b;
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.pwa-content-area {
  flex: 1;
}

/* ===== PWA POS GRID LAYOUT ===== */
.pwa-pos-grid {
  display: grid;
  grid-template-columns: 1fr 400px;
  gap: 1.5rem;
  height: calc(100vh - 280px);
  min-height: 600px;
}

.pwa-menu-section,
.pwa-cart-section {
  display: flex;
  flex-direction: column;
  background: white;
  border-radius: 20px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  overflow: hidden;
}

.pwa-section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.25rem 1.5rem;
  border-bottom: 1px solid #f1f5f9;
  background: linear-gradient(135deg, #ffffff 0%, #f8fafc 100%);
}

.pwa-header-content {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.pwa-header-icon {
  width: 48px;
  height: 48px;
  background: linear-gradient(135deg, #f59e0b, #d97706);
  border-radius: 14px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.25rem;
}

.pwa-cart-icon {
  background: linear-gradient(135deg, #10b981, #059669);
}

.pwa-header-text {
  display: flex;
  flex-direction: column;
}

.pwa-section-title {
  font-size: 1.125rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0;
}

.pwa-section-subtitle {
  color: #64748b;
  font-size: 0.8125rem;
  margin: 0;
}

.pwa-header-actions {
  display: flex;
  gap: 0.5rem;
}

.pwa-refresh-btn {
  width: 40px;
  height: 40px;
  background: none;
  border: 1px solid #e2e8f0;
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #64748b;
  transition: all 0.2s ease;
  cursor: pointer;
}

.pwa-refresh-btn:hover {
  background: #f1f5f9;
  color: #3b82f6;
  border-color: #3b82f6;
}

.pwa-cart-total {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  text-align: right;
}

.pwa-total-label {
  color: #64748b;
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  font-weight: 600;
}

.pwa-total-amount {
  color: #10b981;
  font-size: 1.25rem;
  font-weight: 700;
  line-height: 1.2;
}

.pwa-menu-container,
.pwa-cart-container {
  flex: 1;
  overflow-y: auto;
  padding: 1rem;
}

/* ===== PWA RESPONSIVE DESIGN ===== */
@media (max-width: 1200px) {
  .pwa-pos-grid {
    grid-template-columns: 1fr 350px;
  }
}

@media (max-width: 992px) {
  .pwa-pos-grid {
    grid-template-columns: 1fr;
    grid-template-rows: 1fr auto;
    height: auto;
    min-height: auto;
  }

  .pwa-cart-section {
    order: -1;
    max-height: 400px;
  }

  .pwa-main-header {
    flex-direction: column;
    gap: 1rem;
    align-items: flex-start;
  }

  .pwa-header-user {
    align-self: stretch;
    justify-content: center;
  }
}

@media (max-width: 768px) {
  .pwa-nav-container {
    justify-content: flex-start;
    overflow-x: auto;
    padding-bottom: 0.5rem;
  }

  .pwa-nav-tab {
    min-width: 70px;
    padding: 0.5rem 0.75rem;
  }

  .pwa-tab-label {
    font-size: 0.65rem;
  }

  .pwa-main-content {
    padding: 0 0.75rem;
  }

  .pwa-main-title {
    font-size: 1.5rem;
  }

  .pwa-pos-grid {
    gap: 1rem;
  }

  .pwa-section-header {
    padding: 1rem 1.25rem;
  }

  .pwa-header-icon {
    width: 40px;
    height: 40px;
    font-size: 1.1rem;
  }
}

@media (max-width: 480px) {
  .pwa-nav-tab {
    min-width: 60px;
    padding: 0.5rem;
  }

  .pwa-tab-icon {
    font-size: 1.1rem;
    margin-bottom: 0.125rem;
  }

  .pwa-tab-label {
    font-size: 0.625rem;
  }

  .pwa-main-content {
    padding: 0 0.5rem;
  }

  .pwa-main-header {
    padding: 1rem 0;
  }

  .pwa-header-content {
    gap: 0.75rem;
  }
}

/* ===== PWA MENU GRID STYLES ===== */
.pos-menu-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1.25rem;
  padding: 0.5rem 0;
  width: 100%;
}

.menu-item-card {
  background: white;
  border: 1px solid #f1f5f9;
  border-radius: 16px;
  padding: 1.25rem;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  position: relative;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
}

.menu-item-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, #f59e0b, #d97706);
  transform: scaleX(0);
  transition: transform 0.3s ease;
}

.menu-item-card:hover {
  transform: translateY(-6px);
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
  border-color: #f59e0b;
}

.menu-item-card:hover::before {
  transform: scaleX(1);
}

.menu-item-card:active {
  transform: translateY(-2px) scale(0.98);
}

.menu-item-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 1rem;
  gap: 1rem;
}

.menu-item-name {
  font-size: 1.125rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0;
  line-height: 1.3;
  flex: 1;
}

.menu-item-price {
  background: linear-gradient(135deg, #10b981, #059669);
  color: white;
  font-size: 1.125rem;
  font-weight: 700;
  padding: 0.375rem 0.75rem;
  border-radius: 12px;
  text-align: center;
  box-shadow: 0 2px 8px rgba(16, 185, 129, 0.25);
  white-space: nowrap;
}

.menu-item-stock {
  font-size: 0.8125rem;
  font-weight: 600;
  margin-bottom: 1.25rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  border-radius: 10px;
  background: #f8fafc;
}

.stock-out {
  color: #ef4444 !important;
  background: #fef2f2 !important;
}

.stock-low {
  color: #f59e0b !important;
  background: #fffbeb !important;
}

.stock-good {
  color: #10b981 !important;
  background: #f0fdfa !important;
}

.menu-item-actions {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.quantity-selector {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  background: #f8fafc;
  border-radius: 12px;
  padding: 0.5rem;
}

.qty-btn {
  width: 40px;
  height: 40px;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  border: 1px solid #e2e8f0;
  background: white;
  color: #64748b;
  font-weight: 600;
  transition: all 0.2s ease;
  touch-action: manipulation;
}

.qty-btn:hover:not(:disabled) {
  background: #3b82f6;
  color: white;
  border-color: #3b82f6;
  transform: scale(1.05);
}

.qty-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.qty-input {
  width: 60px;
  height: 40px;
  text-align: center;
  font-weight: 700;
  font-size: 1rem;
  border: 1px solid #e2e8f0;
  border-radius: 10px;
  background: white;
  color: #1e293b;
}

.qty-input:focus {
  border-color: #3b82f6;
  box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  outline: none;
}

.add-to-cart-btn {
  font-weight: 700;
  padding: 0.875rem 1.25rem;
  border-radius: 12px;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  border: none;
  font-size: 0.9375rem;
  position: relative;
  overflow: hidden;
  touch-action: manipulation;
}

.add-to-cart-btn:not(:disabled) {
  background: linear-gradient(135deg, #dc2626, #b91c1c);
  color: white;
  box-shadow: 0 4px 12px rgba(220, 38, 38, 0.3);
}

.add-to-cart-btn:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(220, 38, 38, 0.4);
}

.add-to-cart-btn:active:not(:disabled) {
  transform: translateY(0) scale(0.98);
}

.add-to-cart-btn:disabled {
  background: #e2e8f0;
  color: #94a3b8;
  box-shadow: none;
  cursor: not-allowed;
}

.no-menu-items {
  grid-column: 1 / -1;
  text-align: center;
  padding: 4rem 2rem;
  color: #64748b;
  background: white;
  border-radius: 16px;
  border: 2px dashed #e2e8f0;
}

.no-menu-items i {
  margin-bottom: 1rem;
  color: #cbd5e1;
}

/* ===== PWA CART STYLES ===== */
.pwa-cart-wrapper {
  height: 100%;
  display: flex;
  flex-direction: column;
}

.pwa-cart-container .card {
  background: none;
  border: none;
  box-shadow: none;
}

.pwa-cart-container .card-body {
  padding: 0;
}

.pwa-cart-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.25rem;
  padding: 1rem;
  background: #f8fafc;
  border-radius: 12px;
}

.pwa-cart-title {
  font-size: 1.125rem;
  font-weight: 700;
  color: #1e293b;
  margin: 0;
}

.pwa-cart-actions {
  display: flex;
  gap: 0.5rem;
}

.pwa-cart-btn {
  padding: 0.5rem 0.75rem;
  border-radius: 8px;
  font-weight: 600;
  font-size: 0.875rem;
  border: none;
  transition: all 0.2s ease;
  touch-action: manipulation;
  display: flex;
  align-items: center;
  gap: 0.25rem;
}

.pwa-cart-btn.clear {
  background: #fef2f2;
  color: #dc2626;
}

.pwa-cart-btn.clear:hover:not(:disabled) {
  background: #fee2e2;
  transform: translateY(-1px);
}

.pwa-cart-btn.confirm {
  background: linear-gradient(135deg, #10b981, #059669);
  color: white;
}

.pwa-cart-btn.confirm:hover:not(:disabled) {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(16, 185, 129, 0.3);
}

.pwa-cart-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.pwa-cart-items {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  margin-bottom: 1.25rem;
  min-height: 200px;
  overflow-y: auto;
}

.pwa-cart-empty {
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 2rem 1rem;
  color: #64748b;
}

.pwa-cart-empty i {
  color: #cbd5e1;
  margin-bottom: 0.5rem;
}

.pwa-cart-empty p {
  font-weight: 600;
  margin: 0 0 0.25rem 0;
  color: #475569;
}

.pwa-cart-empty span {
  font-size: 0.875rem;
}

.pwa-cart-item {
  background: white;
  border: 1px solid #f1f5f9;
  border-radius: 12px;
  padding: 1rem;
  transition: all 0.2s ease;
}

.pwa-cart-item:hover {
  border-color: #e2e8f0;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
}

.pwa-item-info {
  margin-bottom: 0.75rem;
}

.pwa-item-name {
  font-size: 0.9375rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0 0 0.25rem 0;
}

.pwa-item-price {
  font-size: 0.8125rem;
  color: #64748b;
}

.pwa-item-controls {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 0.75rem;
}

.pwa-quantity-controls {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: #f8fafc;
  border-radius: 8px;
  padding: 0.25rem;
}

.pwa-qty-btn {
  width: 32px;
  height: 32px;
  border-radius: 6px;
  border: 1px solid #e2e8f0;
  background: white;
  color: #64748b;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.75rem;
  font-weight: 600;
  transition: all 0.2s ease;
  touch-action: manipulation;
}

.pwa-qty-btn:hover {
  background: #3b82f6;
  color: white;
  border-color: #3b82f6;
}

.pwa-qty-input {
  width: 45px;
  height: 32px;
  text-align: center;
  font-weight: 600;
  font-size: 0.875rem;
  border: 1px solid #e2e8f0;
  border-radius: 6px;
  background: white;
  color: #1e293b;
}

.pwa-qty-input:focus {
  border-color: #3b82f6;
  box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.1);
  outline: none;
}

.pwa-item-total {
  font-size: 0.9375rem;
  font-weight: 700;
  color: #10b981;
  min-width: 80px;
  text-align: center;
}

.pwa-remove-btn {
  width: 32px;
  height: 32px;
  border-radius: 6px;
  border: 1px solid #fecaca;
  background: #fef2f2;
  color: #dc2626;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.75rem;
  transition: all 0.2s ease;
  touch-action: manipulation;
}

.pwa-remove-btn:hover {
  background: #fee2e2;
  transform: scale(1.05);
}

.pwa-cart-summary {
  border-top: 2px solid #f1f5f9;
  padding-top: 1rem;
  margin-top: auto;
}

.pwa-cart-total {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  background: linear-gradient(135deg, #f0fdf4, #dcfce7);
  border-radius: 12px;
  border: 1px solid #bbf7d0;
}

.pwa-total-label {
  font-size: 1rem;
  font-weight: 600;
  color: #166534;
}

.pwa-total-value {
  font-size: 1.25rem;
  font-weight: 700;
  color: #15803d;
}

