/* ===== MODERN PWA NAVBAR ===== */
.modern-navbar {
  background: #ffffff;
  border-bottom: 1px solid #e2e8f0;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
  position: sticky;
  top: 0;
  z-index: 1030;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

.navbar-container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.75rem 1.5rem;
  max-width: 1400px;
  margin: 0 auto;
  gap: 2rem;
}

/* Brand Section */
.navbar-brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  background: none;
  border: none;
  padding: 0.5rem;
  border-radius: 12px;
  transition: all 0.2s ease;
  cursor: pointer;
}

.navbar-brand:hover {
  background: rgba(220, 53, 69, 0.05);
  transform: translateY(-1px);
}

.brand-icon {
  position: relative;
}

.brand-logo {
  width: 40px;
  height: 40px;
  border-radius: 10px;
  object-fit: cover;
  box-shadow: 0 4px 12px rgba(220, 53, 69, 0.2);
  transition: all 0.3s ease;
}

.navbar-brand:hover .brand-logo {
  transform: scale(1.05);
  box-shadow: 0 6px 20px rgba(220, 53, 69, 0.3);
}

.brand-content {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}

.brand-title {
  font-size: 1.25rem;
  font-weight: 700;
  color: #000000;
  margin: 0;
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.brand-badge {
  background: #f3f4f6;
  color: #000000;
  font-size: 0.65rem;
  font-weight: 600;
  padding: 0.15rem 0.4rem;
  border-radius: 8px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.brand-subtitle {
  font-size: 0.75rem;
  color: #000000;
  margin: 0;
  font-weight: 500;
}

/* Navigation Tabs */
.nav-tabs {
  flex: 1;
  display: flex;
  justify-content: center;
}

.nav-tab-list {
  display: flex;
  align-items: center;
  background: #f1f5f9;
  border-radius: 12px;
  padding: 0.25rem;
  gap: 0.25rem;
}

.nav-tab {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: transparent;
  border: none;
  padding: 0.75rem 1.25rem;
  border-radius: 10px;
  font-weight: 500;
  font-size: 0.875rem;
  color: #000000;
  transition: all 0.2s ease;
  cursor: pointer;
  position: relative;
}

.nav-tab:hover {
  color: #000000;
  background: rgba(220, 53, 69, 0.08);
}

.nav-tab.active {
  background: #ffffff;
  color: #000000;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  font-weight: 600;
}

.nav-tab.active::after {
  content: '';
  position: absolute;
  bottom: -0.25rem;
  left: 50%;
  transform: translateX(-50%);
  width: 20px;
  height: 3px;
  background: linear-gradient(135deg, #dc3545, #c82333);
  border-radius: 2px;
}

.tab-icon {
  font-size: 1rem;
}

.tab-label {
  font-weight: inherit;
}

/* User Profile */
.user-profile {
  display: flex;
  align-items: center;
}

.user-info {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  position: relative;
}

.user-avatar {
  width: 36px;
  height: 36px;
  background: linear-gradient(135deg, #dc3545, #c82333);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.1rem;
  box-shadow: 0 2px 8px rgba(220, 53, 69, 0.2);
}

.user-details {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}

.user-name {
  font-weight: 600;
  color: #000000;
  font-size: 0.875rem;
  line-height: 1.2;
}

.user-role {
  font-size: 0.75rem;
  font-weight: 500;
  display: flex;
  align-items: center;
  padding: 0.125rem 0.375rem;
  border-radius: 6px;
  margin-top: 0.125rem;
  color: #000000;
}

.role-admin {
  background: #f3f4f6;
  color: #000000;
}

.role-staff {
  background: #f3f4f6;
  color: #000000;
}

.user-menu-btn {
  background: none;
  border: none;
  color: #000000;
  padding: 0.5rem;
  border-radius: 8px;
  transition: all 0.2s ease;
  cursor: pointer;
}

.user-menu-btn:hover {
  background: #f3f4f6;
  color: #dc3545;
}

/* Modern Dropdown */
.modern-dropdown {
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 12px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  padding: 0.5rem;
  min-width: 220px;
  margin-top: 0.5rem;
}

.modern-dropdown .dropdown-header {
  padding: 0.75rem;
  border: none;
  background: none;
}

.dropdown-user-info {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.dropdown-avatar {
  width: 32px;
  height: 32px;
  background: linear-gradient(135deg, #dc3545, #c82333);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1rem;
}

.dropdown-name {
  font-weight: 600;
  color: #000000;
  font-size: 0.875rem;
}

.dropdown-role {
  font-size: 0.75rem;
  font-weight: 500;
  display: flex;
  align-items: center;
  padding: 0.125rem 0.375rem;
  border-radius: 6px;
  margin-top: 0.125rem;
  color: #000000;
  background: #f3f4f6;
}

.logout-item {
  border: none;
  background: none;
  width: 100%;
  padding: 0.75rem;
  border-radius: 8px;
  transition: all 0.2s ease;
  cursor: pointer;
}

.logout-item:hover {
  background: #fee2e2;
  color: #dc2626;
}

.dropdown-item-content {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  font-weight: 500;
}

/* PWA Indicator */
.pwa-indicator {
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  width: 8px;
  height: 8px;
  background: #10b981;
  border-radius: 50%;
  animation: pulse 2s infinite;
}

/* Mobile Responsive Styles */
@media (max-width: 768px) {
  .navbar-container {
    padding: 0.5rem 1rem;
    gap: 1rem;
  }

  .brand-logo {
    width: 32px;
    height: 32px;
  }

  .brand-title {
    font-size: 1.1rem;
  }

  .brand-badge {
    font-size: 0.6rem;
    padding: 0.1rem 0.3rem;
  }

  .brand-subtitle {
    display: none;
  }

  .nav-tab {
    padding: 0.6rem 1rem;
    font-size: 0.8rem;
  }

  .tab-label {
    display: none;
  }

  .user-details {
    display: none !important;
  }

  .user-avatar {
    width: 32px;
    height: 32px;
    font-size: 1rem;
  }
}

@media (max-width: 480px) {
  .navbar-container {
    padding: 0.5rem 0.75rem;
  }

  .brand-title {
    font-size: 1rem;
  }

  .nav-tab-list {
    padding: 0.2rem;
    gap: 0.2rem;
  }

  .nav-tab {
    padding: 0.5rem 0.8rem;
  }

  .tab-icon {
    font-size: 0.9rem;
  }

  .modern-dropdown {
    min-width: 180px;
  }
}

/* Dark mode support - Override to maintain white background and black text */
@media (prefers-color-scheme: dark) {
  .modern-navbar {
    background: #ffffff !important;
    border-bottom: 1px solid #e2e8f0 !important;
  }

  .brand-title {
    color: #000000 !important;
  }

  .brand-subtitle {
    color: #000000 !important;
  }

  .nav-tab-list {
    background: #f1f5f9 !important;
  }

  .nav-tab {
    color: #000000 !important;
  }

  .nav-tab:hover {
    background: rgba(220, 53, 69, 0.08) !important;
    color: #000000 !important;
  }

  .nav-tab.active {
    background: #ffffff !important;
    color: #000000 !important;
  }

  .user-name {
    color: #000000 !important;
  }

  .modern-dropdown {
    background: white !important;
    border: 1px solid #e5e7eb !important;
  }

  .dropdown-name {
    color: #000000 !important;
  }

  .mobile-menu-toggle {
    color: #000000 !important;
  }

  .notification-btn {
    color: #000000 !important;
  }

  .user-menu-btn {
    color: #000000 !important;
  }

  .logout-item:hover {
    background: #fee2e2 !important;
    color: #dc2626 !important;
  }
}

/* PWA-specific enhancements */
@supports (display: standalone) {
  .modern-navbar {
    padding-top: env(safe-area-inset-top);
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .modern-navbar {
    border-bottom: 2px solid #000;
  }

  .nav-tab.active {
    outline: 2px solid currentColor;
  }
}

