import React, { useState, useEffect } from 'react';
import { getStaff, addStaff, getMenu, addMenuItem, updateMenuStock, getOrders } from '../services/api';
import { useAuth } from '../services/AuthContext';

const Admin = () => {
  const { user } = useAuth();
  const [staff, setStaff] = useState([]);
  const [menu, setMenu] = useState([]);
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeSection, setActiveSection] = useState('dashboard');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Form states
  const [newStaff, setNewStaff] = useState({ username: '', password: '', role: 'staff' });
  const [newMenuItem, setNewMenuItem] = useState({ name: '', price: '', stock: '' });
  const [stockUpdates, setStockUpdates] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    loadAllData();
  }, []);

  // Mobile detection and responsive handling
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Close mobile menu when section changes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [activeSection]);

  const loadAllData = async () => {
    try {
      setIsLoading(true);
      const [staffData, menuData, ordersData] = await Promise.all([
        getStaff(),
        getMenu(),
        getOrders()
      ]);
      setStaff(staffData);
      setMenu(menuData);
      setOrders(ordersData);
      setError('');
    } catch (error) {
      console.error('Error loading admin data:', error);
      setError('Failed to load admin data. Please refresh.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddStaff = async (e) => {
    e.preventDefault();
    if (!newStaff.username || !newStaff.password) {
      alert('Please fill in all fields');
      return;
    }

    try {
      await addStaff(newStaff);
      await loadAllData();
      setNewStaff({ username: '', password: '', role: 'staff' });
      setSuccessMessage('Staff member added successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (error) {
      console.error('Error adding staff:', error);
      setError('Failed to add staff member');
    }
  };

  const handleAddMenuItem = async (e) => {
    e.preventDefault();
    if (!newMenuItem.name || !newMenuItem.price || !newMenuItem.stock) {
      setError('Please fill in all fields');
      return;
    }

    try {
      await addMenuItem({
        name: newMenuItem.name,
        price: parseFloat(newMenuItem.price),
        stock: parseInt(newMenuItem.stock)
      });
      await loadAllData();
      setNewMenuItem({ name: '', price: '', stock: '' });
      setSuccessMessage('Menu item added successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (error) {
      console.error('Error adding menu item:', error);
      setError('Failed to add menu item');
    }
  };

  const handleUpdateStock = async (itemId, newStock) => {
    try {
      await updateMenuStock(itemId, parseInt(newStock));
      await loadAllData();
      setStockUpdates({});
      setSuccessMessage('Stock updated successfully!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (error) {
      console.error('Error updating stock:', error);
      setError('Failed to update stock');
    }
  };

  const handleStockChange = (itemId, value) => {
    setStockUpdates(prev => ({
      ...prev,
      [itemId]: value
    }));
  };

  const exportData = () => {
    const data = {
      staff,
      menu,
      orders,
      exportDate: new Date().toISOString()
    };

    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `shawarma-boss-export-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const exportCSV = () => {
    const csvContent = [
      ['Order ID', 'Staff', 'Date', 'Total', 'Items'].join(','),
      ...orders.map(order => [
        order.id,
        order.staff,
        order.timestamp,
        order.total,
        order.payload ? order.payload.items?.map(item => `${item.name} x${item.quantity}`).join(';') : ''
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `orders-export-${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  // Calculate sales stats
  const salesStats = React.useMemo(() => {
    const today = new Date().toDateString();
    const todayOrders = orders.filter(order => 
      new Date(order.timestamp).toDateString() === today
    );
    const totalSales = orders.reduce((sum, order) => sum + Number(order.total || 0), 0);
    const todaySales = todayOrders.reduce((sum, order) => sum + Number(order.total || 0), 0);

    const staffSales = {};
    orders.forEach(order => {
      if (!staffSales[order.staff]) {
        staffSales[order.staff] = { orders: 0, total: 0 };
      }
      staffSales[order.staff].orders++;
      staffSales[order.staff].total += Number(order.total || 0);
    });

    return {
      totalOrders: orders.length,
      totalSales,
      todayOrders: todayOrders.length,
      todaySales,
      staffSales
    };
  }, [orders]);

  // Sidebar navigation items
  const sidebarItems = [
    { id: 'dashboard', icon: 'fas fa-tachometer-alt', label: 'Dashboard' },
    { id: 'staff', icon: 'fas fa-users', label: 'Staff Management' },
    { id: 'menu', icon: 'fas fa-utensils', label: 'Menu Management' },
    { id: 'orders', icon: 'fas fa-receipt', label: 'Recent Orders' },
    { id: 'reports', icon: 'fas fa-chart-bar', label: 'Reports & Export' }
  ];

  const renderDashboard = () => (
    <div className="dashboard-container">
      {/* KPI Grid Cards */}
      <div className="kpi-grid d-flex flex-wrap gap-3 mb-4">
        <div className="kpi-card flex-fill">
          <div className="kpi-icon bg-primary">
            <i className="fas fa-shopping-cart"></i>
          </div>
          <div className="kpi-content">
            <div className="kpi-value text-primary">{salesStats.totalOrders}</div>
            <div className="kpi-label">Total Orders</div>
          </div>
        </div>
        <div className="kpi-card flex-fill">
          <div className="kpi-icon bg-success">
            <i className="fas fa-dollar-sign"></i>
          </div>
          <div className="kpi-content">
            <div className="kpi-value text-success">GHS {salesStats.totalSales.toFixed(2)}</div>
            <div className="kpi-label">Total Sales</div>
          </div>
        </div>
        <div className="kpi-card flex-fill">
          <div className="kpi-icon bg-warning">
            <i className="fas fa-clock"></i>
          </div>
          <div className="kpi-content">
            <div className="kpi-value text-warning">{salesStats.todayOrders}</div>
            <div className="kpi-label">Today's Orders</div>
          </div>
        </div>
        <div className="kpi-card flex-fill">
          <div className="kpi-icon bg-info">
            <i className="fas fa-chart-line"></i>
          </div>
          <div className="kpi-content">
            <div className="kpi-value text-info">GHS {salesStats.todaySales.toFixed(2)}</div>
            <div className="kpi-label">Today's Sales</div>
          </div>
        </div>
      </div>

      {/* Responsive Grid Layout for Staff and Stock */}
      <div className="admin-grid-layout d-flex flex-column flex-lg-row gap-4">
        {/* Staff Performance Panel */}
        <div className="admin-panel flex-fill">
          <div className="panel-header">
            <h4 className="panel-title">
              <i className="fas fa-users me-2"></i>
              Staff Performance
            </h4>
          </div>
          <div className="panel-body">
            <div className="staff-grid d-flex flex-wrap gap-3">
              {staff.map((member) => (
                <div key={member.username} className="staff-card flex-fill">
                  <div className="staff-avatar">
                    <i className="fas fa-user"></i>
                  </div>
                  <div className="staff-info">
                    <div className="staff-name">{member.username}</div>
                    <div className="staff-role">{member.role}</div>
                  </div>
                  {salesStats.staffSales[member.username] && (
                    <div className="staff-stats">
                      <div className="stat-orders">{salesStats.staffSales[member.username].orders}</div>
                      <div className="stat-sales">GHS {salesStats.staffSales[member.username].total.toFixed(2)}</div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Stock Alerts Panel */}
        <div className="admin-panel flex-fill">
          <div className="panel-header">
            <h4 className="panel-title">
              <i className="fas fa-exclamation-triangle me-2"></i>
              Stock Alerts
            </h4>
          </div>
          <div className="panel-body">
            {menu.filter(item => item.stock <= 5).length === 0 ? (
              <div className="no-alerts">
                <i className="fas fa-check-circle"></i>
                <span>All items are well stocked</span>
              </div>
            ) : (
              <div className="stock-alerts d-flex flex-column gap-2">
                {menu.filter(item => item.stock <= 5).map(item => (
                  <div key={item.id} className={`stock-alert ${item.stock === 0 ? 'critical' : 'warning'}`}>
                    <div className="alert-icon">
                      <i className={`fas ${item.stock === 0 ? 'fa-times-circle' : 'fa-exclamation-triangle'}`}></i>
                    </div>
                    <div className="alert-content">
                      <div className="alert-title">{item.name}</div>
                      <div className="alert-message">{item.stock === 0 ? 'Out of stock' : `${item.stock} left`}</div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );

  const renderStaffManagement = () => (
    <div className="staff-management-container">
      <div className="admin-grid-layout d-flex flex-column flex-xl-row gap-4">
        {/* Staff List Panel */}
        <div className="admin-panel flex-fill">
          <div className="panel-header">
            <h4 className="panel-title">
              <i className="fas fa-users me-2"></i>
              Staff Members
            </h4>
          </div>
          <div className="panel-body">
            <div className="staff-management-grid d-flex flex-wrap gap-3">
              {staff.map((member) => (
                <div key={member.username} className="staff-management-card flex-fill">
                  <div className="staff-card-header">
                    <div className="staff-avatar large">
                      <i className="fas fa-user"></i>
                    </div>
                    <div className="staff-badge">
                      <span className={`badge ${member.role === 'admin' ? 'bg-danger' : 'bg-primary'}`}>
                        {member.role}
                      </span>
                    </div>
                  </div>
                  <div className="staff-card-body">
                    <div className="staff-name">{member.username}</div>
                    {salesStats.staffSales[member.username] && (
                      <div className="staff-performance">
                        <div className="performance-item">
                          <span className="performance-label">Orders:</span>
                          <span className="performance-value">{salesStats.staffSales[member.username].orders}</span>
                        </div>
                        <div className="performance-item">
                          <span className="performance-label">Sales:</span>
                          <span className="performance-value text-success">GHS {salesStats.staffSales[member.username].total.toFixed(2)}</span>
                        </div>
                      </div>
                    )}
                  </div>
                  <div className="staff-card-actions">
                    <button className="btn btn-sm btn-outline-primary">
                      <i className="fas fa-edit"></i>
                    </button>
                    <button className="btn btn-sm btn-outline-danger">
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Add Staff Form Panel */}
        <div className="admin-panel" style={{minWidth: '320px'}}>
          <div className="panel-header">
            <h4 className="panel-title">
              <i className="fas fa-user-plus me-2"></i>
              Add New Staff
            </h4>
          </div>
          <div className="panel-body">
            <form onSubmit={handleAddStaff} className="add-staff-form">
              <div className="form-group">
                <label className="form-label">Username</label>
                <input
                  type="text"
                  placeholder="Enter username"
                  value={newStaff.username}
                  onChange={(e) => setNewStaff({...newStaff, username: e.target.value})}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Password</label>
                <input
                  type="password"
                  placeholder="Enter password"
                  value={newStaff.password}
                  onChange={(e) => setNewStaff({...newStaff, password: e.target.value})}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label className="form-label">Role</label>
                <select
                  value={newStaff.role}
                  onChange={(e) => setNewStaff({...newStaff, role: e.target.value})}
                  className="form-control"
                >
                  <option value="staff">Staff</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
              <button type="submit" className="btn btn-danger w-100">
                <i className="fas fa-user-plus me-2"></i>
                Add Staff Member
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );

  const renderMenuManagement = () => (
    <div className="row">
      <div className="col-lg-8">
        <div className="card">
          <div className="card-header">
            <h4 className="font-bold text-danger mb-0">Menu Items</h4>
          </div>
          <div className="card-body">
            <div className="table-responsive">
              <table className="table table-hover">
                <thead className="table-light">
                  <tr>
                    <th>Item Name</th>
                    <th>Price</th>
                    <th>Stock</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {menu.map((item) => (
                    <tr key={item.id}>
                      <td className="fw-medium">{item.name}</td>
                      <td>GHS {parseFloat(item.price || 0).toFixed(2)}</td>
                      <td>
                        <div className="d-flex align-items-center gap-2">
                          <input
                            type="number"
                            min="0"
                            value={stockUpdates[item.id] !== undefined ? stockUpdates[item.id] : item.stock}
                            onChange={(e) => handleStockChange(item.id, e.target.value)}
                            className="form-control form-control-sm"
                            style={{ width: '80px' }}
                          />
                          {stockUpdates[item.id] !== undefined && stockUpdates[item.id] != item.stock && (
                            <button
                              onClick={() => handleUpdateStock(item.id, stockUpdates[item.id])}
                              className="btn btn-sm btn-primary"
                            >
                              Update
                            </button>
                          )}
                        </div>
                      </td>
                      <td>
                        <span className={`badge ${item.stock <= 0 ? 'bg-danger' : item.stock <= 5 ? 'bg-warning' : 'bg-success'}`}>
                          {item.stock <= 0 ? 'Out of Stock' : item.stock <= 5 ? 'Low Stock' : 'In Stock'}
                        </span>
                      </td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary me-2">
                          <i className="fas fa-edit"></i>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>

      <div className="col-lg-4">
        <div className="card">
          <div className="card-header">
            <h4 className="font-bold text-danger mb-0">Add New Item</h4>
          </div>
          <div className="card-body">
            <form onSubmit={handleAddMenuItem}>
              <div className="mb-3">
                <input
                  type="text"
                  placeholder="Item name"
                  value={newMenuItem.name}
                  onChange={(e) => setNewMenuItem({...newMenuItem, name: e.target.value})}
                  className="form-control"
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  step="0.01"
                  placeholder="Price (GHS)"
                  value={newMenuItem.price}
                  onChange={(e) => setNewMenuItem({...newMenuItem, price: e.target.value})}
                  className="form-control"
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  placeholder="Initial Stock"
                  value={newMenuItem.stock}
                  onChange={(e) => setNewMenuItem({...newMenuItem, stock: e.target.value})}
                  className="form-control"
                />
              </div>
              <button type="submit" className="btn btn-danger w-100">
                <i className="fas fa-plus me-2"></i>
                Add Item
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );

  const renderRecentOrders = () => (
    <div className="card">
      <div className="card-header">
        <h4 className="font-bold text-danger mb-0">Recent Orders</h4>
      </div>
      <div className="card-body">
        <div className="table-responsive">
          <table className="table table-hover">
            <thead className="table-light">
              <tr>
                <th>Order ID</th>
                <th>Staff</th>
                <th>Date & Time</th>
                <th>Total</th>
                <th>Items</th>
              </tr>
            </thead>
            <tbody>
              {orders.slice(0, 20).map((order) => (
                <tr key={order.id}>
                  <td className="font-mono">#{order.id.slice(-8)}</td>
                  <td>{order.staff}</td>
                  <td>{new Date(order.timestamp).toLocaleString()}</td>
                  <td className="fw-bold text-success">GHS {parseFloat(order.total || 0).toFixed(2)}</td>
                  <td className="text-sm">
                    {order.payload?.items?.slice(0, 2).map(item => item.name).join(', ')}
                    {order.payload?.items?.length > 2 && '...'}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {orders.length === 0 && (
            <div className="text-center py-4 text-muted">No orders yet</div>
          )}
        </div>
      </div>
    </div>
  );

  const renderReportsExport = () => (
    <div className="row">
      <div className="col-lg-6">
        <div className="card mb-4">
          <div className="card-header">
            <h4 className="font-bold text-danger mb-0">Export Options</h4>
          </div>
          <div className="card-body">
            <div className="d-grid gap-2">
              <button onClick={exportData} className="btn btn-outline-danger">
                <i className="fas fa-download me-2"></i>
                Export JSON Data
              </button>
              <button onClick={exportCSV} className="btn btn-outline-danger">
                <i className="fas fa-file-csv me-2"></i>
                Export Orders CSV
              </button>
              <button onClick={loadAllData} className="btn btn-outline-danger">
                <i className="fas fa-sync-alt me-2"></i>
                Refresh All Data
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="col-lg-6">
        <div className="card">
          <div className="card-header">
            <h4 className="font-bold text-danger mb-0">Quick Stats</h4>
          </div>
          <div className="card-body">
            <div className="row text-center">
              <div className="col-6 mb-3">
                <div className="h4 text-primary mb-0">{menu.length}</div>
                <small className="text-muted">Menu Items</small>
              </div>
              <div className="col-6 mb-3">
                <div className="h4 text-warning mb-0">{staff.length}</div>
                <small className="text-muted">Staff Members</small>
              </div>
              <div className="col-6">
                <div className="h4 text-info mb-0">{menu.filter(item => item.stock <= 5).length}</div>
                <small className="text-muted">Low Stock Items</small>
              </div>
              <div className="col-6">
                <div className="h4 text-success mb-0">{salesStats.todayOrders}</div>
                <small className="text-muted">Today's Orders</small>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'dashboard':
        return renderDashboard();
      case 'staff':
        return renderStaffManagement();
      case 'menu':
        return renderMenuManagement();
      case 'orders':
        return renderRecentOrders();
      case 'reports':
        return renderReportsExport();
      default:
        return renderDashboard();
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="spinner"></div>
        <span className="ml-2">Loading admin panel...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div className="card-body text-center">
          <div className="text-danger mb-4">{error}</div>
          <button onClick={loadAllData} className="btn btn-danger">
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`admin-layout ${isMobile ? 'mobile' : ''}`}>
      {/* Mobile Header */}
      {isMobile && (
        <div className="mobile-header">
          <div className="mobile-header-content">
            <button 
              className="mobile-menu-toggle"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              aria-label="Toggle navigation menu"
            >
              <i className={`fas ${isMobileMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
            </button>
            <h2 className="mobile-page-title">
              {sidebarItems.find(item => item.id === activeSection)?.label || 'Dashboard'}
            </h2>
            <div className="mobile-user-info">
              <div className="mobile-user-avatar">
                <i className="fas fa-user"></i>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Mobile Menu Overlay */}
      {isMobile && isMobileMenuOpen && (
        <div className="mobile-menu-overlay" onClick={() => setIsMobileMenuOpen(false)}>
          <div className="mobile-menu" onClick={(e) => e.stopPropagation()}>
            <div className="mobile-menu-header">
              <div className="mobile-brand">
                <i className="fas fa-shield-alt"></i>
                <span>Admin Panel</span>
              </div>
              <button 
                className="mobile-menu-close"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                <i className="fas fa-times"></i>
              </button>
            </div>
            <nav className="mobile-nav">
              {sidebarItems.map((item) => (
                <button
                  key={item.id}
                  className={`mobile-nav-item ${activeSection === item.id ? 'active' : ''}`}
                  onClick={() => setActiveSection(item.id)}
                >
                  <div className="mobile-nav-icon">
                    <i className={item.icon}></i>
                  </div>
                  <span className="mobile-nav-label">{item.label}</span>
                  <i className="fas fa-chevron-right mobile-nav-arrow"></i>
                </button>
              ))}
            </nav>
          </div>
        </div>
      )}

      {/* Desktop Sidebar */}
      <div className={`admin-sidebar ${isMobile ? 'mobile-hidden' : ''}`}>
        <div className="sidebar-header">
          <div className="sidebar-brand">
            <i className="fas fa-shield-alt me-2"></i>
            <span className="brand-text">Admin Panel</span>
          </div>
        </div>
        <nav className="sidebar-nav">
          {sidebarItems.map((item) => (
            <button
              key={item.id}
              className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
              onClick={() => setActiveSection(item.id)}
            >
              <div className="nav-icon">
                <i className={item.icon}></i>
              </div>
              <span className="nav-label">{item.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content Area */}
      <div className="admin-content">
        {/* Desktop Header */}
        {!isMobile && (
          <div className="content-header">
            <h3 className="page-title">
              {sidebarItems.find(item => item.id === activeSection)?.label || 'Dashboard'}
            </h3>
          </div>
        )}

        <div className="content-body">
          {/* Success/Error Messages */}
          {successMessage && (
            <div className="alert alert-success alert-dismissible fade show mb-4" role="alert">
              <i className="fas fa-check-circle me-2"></i>
              {successMessage}
              <button type="button" className="btn-close" onClick={() => setSuccessMessage('')}></button>
            </div>
          )}

          {error && (
            <div className="alert alert-danger alert-dismissible fade show mb-4" role="alert">
              <i className="fas fa-exclamation-circle me-2"></i>
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          {renderContent()}
        </div>
      </div>

      {/* Mobile Bottom Navigation */}
      {isMobile && (
        <div className="mobile-bottom-nav">
          {sidebarItems.slice(0, 4).map((item) => (
            <button
              key={item.id}
              className={`bottom-nav-item ${activeSection === item.id ? 'active' : ''}`}
              onClick={() => setActiveSection(item.id)}
            >
              <div className="bottom-nav-icon">
                <i className={item.icon}></i>
              </div>
              <span className="bottom-nav-label">{item.label}</span>
            </button>
          ))}
          <button
            className="bottom-nav-item"
            onClick={() => setIsMobileMenuOpen(true)}
          >
            <div className="bottom-nav-icon">
              <i className="fas fa-ellipsis-h"></i>
            </div>
            <span className="bottom-nav-label">More</span>
          </button>
        </div>
      )}
    </div>
  );
};

export default Admin;

src/components/Cart.js.content:
import React from 'react';
import { useCart } from '../services/CartContext';

const Cart = ({ onConfirmOrder }) => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity, 
    clearCart, 
    getCartTotal, 
    getCartCount 
  } = useCart();

  const handleQuantityChange = (itemId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(itemId);
    } else {
      updateQuantity(itemId, newQuantity);
    }
  };

  return (
    <div className="pwa-cart-wrapper">
      <div className="pwa-cart-header">
        <h4 className="pwa-cart-title">Cart ({getCartCount()})</h4>
        <div className="pwa-cart-actions">
          <button
            onClick={clearCart}
            disabled={cartItems.length === 0}
            className="pwa-cart-btn clear"
          >
            <i className="fas fa-trash me-1"></i>
            Clear
          </button>
          <button
            onClick={onConfirmOrder}
            disabled={cartItems.length === 0}
            className="pwa-cart-btn confirm"
          >
            <i className="fas fa-check me-1"></i>
            Confirm
          </button>
        </div>
      </div>

        <div className="pwa-cart-items">
          {cartItems.length === 0 ? (
            <div className="pwa-cart-empty">
              <i className="fas fa-shopping-cart fa-2x mb-2"></i>
              <p>Your cart is empty</p>
              <span className="text-muted">Add items from the menu to get started</span>
            </div>
          ) : (
            cartItems.map((item) => (
              <div key={item.id} className="pwa-cart-item">
                <div className="pwa-item-info">
                  <h6 className="pwa-item-name">{item.name}</h6>
                  <span className="pwa-item-price">
                    GHS {parseFloat(item.price || 0).toFixed(2)} each
                  </span>
                </div>

                <div className="pwa-item-controls">
                  <div className="pwa-quantity-controls">
                    <button
                      onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                      className="pwa-qty-btn"
                    >
                      <i className="fas fa-minus"></i>
                    </button>

                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value) || 1)}
                      className="pwa-qty-input"
                    />

                    <button
                      onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                      className="pwa-qty-btn"
                    >
                      <i className="fas fa-plus"></i>
                    </button>
                  </div>

                  <div className="pwa-item-total">
                    GHS {(parseFloat(item.price || 0) * item.quantity).toFixed(2)}
                  </div>

                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="pwa-remove-btn"
                    title="Remove item"
                  >
                    <i className="fas fa-trash"></i>
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        {cartItems.length > 0 && (
          <div className="pwa-cart-summary">
            <div className="pwa-cart-total">
              <span className="pwa-total-label">Order Total:</span>
              <span className="pwa-total-value">
                GHS {getCartTotal().toFixed(2)}
              </span>
            </div>
          </div>
        )}
    </div>
  );
};

export default Cart;

src/components/Header.js.content:
import React, { useState, useEffect } from 'react';
import { useAuth } from '../services/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';

const Header = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showNotifications, setShowNotifications] = useState(false);

  // Mock notification system for UI demonstration (ready for real-time integration)
  useEffect(() => {
    // Demo notifications - can be replaced with WebSocket/SSE for real-time updates
    const mockNotifications = [
      { id: 1, type: 'order', message: 'New order #1234 received', time: '2 min ago', read: false },
      { id: 2, type: 'system', message: 'Daily backup completed', time: '1 hour ago', read: true },
      { id: 3, type: 'inventory', message: 'Low stock alert: Chicken Shawarma', time: '3 hours ago', read: false },
    ];
    setNotifications(mockNotifications);
    setUnreadCount(mockNotifications.filter(n => !n.read).length);

    // TODO: Replace with real-time notification subscription
    // Example: WebSocket connection, Server-Sent Events, or polling
  }, []);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showNotifications && !event.target.closest('.notification-wrapper')) {
        setShowNotifications(false);
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, [showNotifications]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleNavigation = (path) => {
    navigate(path);
    setIsMobileMenuOpen(false); // Close mobile menu when navigating
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleNotifications = () => {
    setShowNotifications(!showNotifications);
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, read: true })));
    setUnreadCount(0);
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'order': return 'fas fa-shopping-cart';
      case 'system': return 'fas fa-cog';
      case 'inventory': return 'fas fa-exclamation-triangle';
      default: return 'fas fa-bell';
    }
  };

  return (
    <header className="modern-navbar">
      <div className="navbar-container">
        {/* Mobile Hamburger Menu */}
        {user && (
          <button 
            className="mobile-menu-toggle d-md-none"
            onClick={toggleMobileMenu}
            aria-label="Toggle mobile menu"
            aria-expanded={isMobileMenuOpen}
          >
            <i className={`fas ${isMobileMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
          </button>
        )}

        {/* Brand Section */}
        <button 
          className="navbar-brand" 
          onClick={() => handleNavigation('/')}
          aria-label="Go to home"
        >
          <div className="brand-icon">
            <img 
              src="/icons/logo.png" 
              alt="Shawarma Boss Logo" 
              className="brand-logo"
            />
          </div>
          <div className="brand-content">
            <h1 className="brand-title">
              Shawarma Boss
              <span className="brand-badge">POS</span>
            </h1>
            <p className="brand-subtitle d-none d-lg-block">Modern Point of Sale</p>
          </div>
        </button>

        {user && (
          <>
            {/* Desktop Navigation Tabs */}
            <nav className="nav-tabs d-none d-md-flex" aria-label="Primary navigation">
              <div className="nav-tab-list">
                <button
                  onClick={() => handleNavigation('/')}
                  className={`nav-tab ${location.pathname === '/' ? 'active' : ''}`}
                  title="Staff Dashboard"
                  aria-current={location.pathname === '/' ? 'page' : undefined}
                >
                  <div className="tab-icon">
                    <i className="fas fa-cash-register"></i>
                  </div>
                  <span className="tab-label">Staff</span>
                </button>
                {user.role === 'admin' && (
                  <button
                    onClick={() => handleNavigation('/admin')}
                    className={`nav-tab ${location.pathname === '/admin' ? 'active' : ''}`}
                    title="Admin Dashboard"
                    aria-current={location.pathname === '/admin' ? 'page' : undefined}
                  >
                    <div className="tab-icon">
                      <i className="fas fa-shield-alt"></i>
                    </div>
                    <span className="tab-label">Admin</span>
                  </button>
                )}
              </div>
            </nav>

            {/* Right Side Actions */}
            <div className="navbar-actions">
              {/* Notifications */}
              <div className="notification-wrapper">
                <button
                  className="notification-btn"
                  onClick={toggleNotifications}
                  title="Notifications"
                  aria-label="Show notifications"
                >
                  <i className="fas fa-bell"></i>
                  {unreadCount > 0 && (
                    <span className="notification-badge">{unreadCount}</span>
                  )}
                </button>

                {/* Notification Dropdown */}
                {showNotifications && (
                  <div className="notification-dropdown">
                    <div className="notification-header">
                      <h6>Notifications</h6>
                      {unreadCount > 0 && (
                        <button 
                          className="mark-read-btn"
                          onClick={markAllAsRead}
                        >
                          Mark all as read
                        </button>
                      )}
                    </div>
                    <div className="notification-list">
                      {notifications.length === 0 ? (
                        <div className="notification-empty">
                          <i className="fas fa-bell-slash"></i>
                          <p>No notifications yet</p>
                        </div>
                      ) : (
                        notifications.map(notification => (
                          <div 
                            key={notification.id} 
                            className={`notification-item ${!notification.read ? 'unread' : ''}`}
                          >
                            <div className="notification-icon">
                              <i className={getNotificationIcon(notification.type)}></i>
                            </div>
                            <div className="notification-content">
                              <p className="notification-message">{notification.message}</p>
                              <span className="notification-time">{notification.time}</span>
                            </div>
                            {!notification.read && <div className="unread-dot"></div>}
                          </div>
                        ))
                      )}
                    </div>
                  </div>
                )}
              </div>

              {/* User Profile */}
              <div className="user-profile">
                <div className="user-info">
                  <div className="user-avatar">
                    <i className="fas fa-user-circle"></i>
                  </div>
                  <div className="user-details d-none d-lg-block">
                    <div className="user-name">{user.username}</div>
                    <div className={`user-role role-${user.role}`}>
                      <i className={`fas ${user.role === 'admin' ? 'fa-crown' : 'fa-user'} me-1`}></i>
                      {user.role}
                    </div>
                  </div>

                  <div className="dropdown">
                    <button 
                      className="user-menu-btn" 
                      type="button" 
                      data-bs-toggle="dropdown" 
                      aria-expanded="false"
                      title="User Menu"
                    >
                      <i className="fas fa-chevron-down d-none d-md-inline"></i>
                      <i className="fas fa-ellipsis-v d-md-none"></i>
                    </button>
                    <ul className="dropdown-menu dropdown-menu-end modern-dropdown">
                      <li className="dropdown-header">
                        <div className="dropdown-user-info">
                          <div className="dropdown-avatar">
                            <i className="fas fa-user-circle"></i>
                          </div>
                          <div>
                            <div className="dropdown-name">{user.username}</div>
                            <div className={`dropdown-role role-${user.role}`}>
                              <i className={`fas ${user.role === 'admin' ? 'fa-crown' : 'fa-user'} me-1`}></i>
                              {user.role}
                            </div>
                          </div>
                        </div>
                      </li>
                      <li><hr className="dropdown-divider" /></li>
                      <li>
                        <button className="dropdown-item logout-item" onClick={handleLogout}>
                          <div className="dropdown-item-content">
                            <i className="fas fa-sign-out-alt"></i>
                            <span>Sign Out</span>
                          </div>
                        </button>
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          </>
        )}
      </div>

      {/* Mobile Navigation Menu */}
      {user && (
        <div className={`mobile-nav-menu ${isMobileMenuOpen ? 'open' : ''}`}>
          <div className="mobile-nav-header">
            <div className="mobile-user-info">
              <div className="mobile-avatar">
                <i className="fas fa-user-circle"></i>
              </div>
              <div className="mobile-user-details">
                <div className="mobile-user-name">{user.username}</div>
                <div className={`mobile-user-role role-${user.role}`}>
                  <i className={`fas ${user.role === 'admin' ? 'fa-crown' : 'fa-user'} me-1`}></i>
                  {user.role}
                </div>
              </div>
            </div>
          </div>

          <nav className="mobile-nav-list">
            <button
              onClick={() => handleNavigation('/')}
              className={`mobile-nav-item ${location.pathname === '/' ? 'active' : ''}`}
            >
              <i className="fas fa-cash-register"></i>
              <span>Staff Dashboard</span>
            </button>
            {user.role === 'admin' && (
              <button
                onClick={() => handleNavigation('/admin')}
                className={`mobile-nav-item ${location.pathname === '/admin' ? 'active' : ''}`}
              >
                <i className="fas fa-shield-alt"></i>
                <span>Admin Dashboard</span>
              </button>
            )}
            <div className="mobile-nav-divider"></div>
            <button className="mobile-nav-item logout" onClick={handleLogout}>
              <i className="fas fa-sign-out-alt"></i>
              <span>Sign Out</span>
            </button>
          </nav>
        </div>
      )}

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && <div className="mobile-menu-overlay" onClick={toggleMobileMenu}></div>}
    </header>
  );
};

export default Header;

src/components/Login.js.content:
import React, { useState } from 'react';
import { useAuth } from '../services/AuthContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    if (!username.trim() || !password.trim()) {
      setError('Please enter both username and password');
      setIsLoading(false);
      return;
    }

    try {
      const result = await login(username.trim(), password);
      if (!result.success) {
        setError(result.error || 'Login failed');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-96">
      <div className="card w-full max-w-md slide-up">
        <div className="card-body p-6">
          <div className="text-center mb-6">
            <img 
              src="/icons/logo.png" 
              alt="Shawarma Boss Logo" 
              className="w-12 h-12 mx-auto mb-4 rounded"
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
            <h2 className="text-xl font-bold text-danger">Shawarma Boss POS</h2>
            <p className="text-muted text-sm mt-1">Modern MERN Stack Point of Sale</p>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Username</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="form-control"
                placeholder="Enter username"
                disabled={isLoading}
                autoComplete="username"
              />
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="form-control"
                placeholder="Enter password"
                disabled={isLoading}
                autoComplete="current-password"
              />
            </div>

            {error && (
              <div className="text-danger text-sm mb-4 p-2 bg-red-50 border border-red-200 rounded">
                {error}
              </div>
            )}

            <button
              type="submit"
              disabled={isLoading}
              className="btn btn-danger w-full flex items-center justify-center"
            >
              {isLoading ? (
                <div className="spinner w-4 h-4"></div>
              ) : (
                'Login'
              )}
            </button>
          </form>

          <div className="mt-6 text-center text-sm text-muted">
            <div className="mb-2">
              <strong>Demo Accounts:</strong>
            </div>
            <div className="space-y-1">
              <div>Admin: <code>admin</code> / <code>admin123</code></div>
              <div>Staff: <code>staff1</code> / <code>staff123</code></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;

src/components/MenuGrid.js.content:
import React, { useState } from 'react';
import { useCart } from '../services/CartContext';

const MenuGrid = ({ menu }) => {
  const { addToCart } = useCart();
  const [quantities, setQuantities] = useState({});

  const handleQuantityChange = (itemId, quantity) => {
    setQuantities(prev => ({
      ...prev,
      [itemId]: Math.max(1, parseInt(quantity) || 1)
    }));
  };

  const handleAddToCart = (item) => {
    const quantity = quantities[item.id] || 1;
    if (item.stock < quantity) {
      alert(`Not enough stock! Only ${item.stock} available.`);
      return;
    }
    addToCart(item, quantity);
    // Reset quantity to 1 after adding
    setQuantities(prev => ({
      ...prev,
      [item.id]: 1
    }));
  };

  const getStockStatus = (stock) => {
    if (stock === 0) return { text: 'Out of Stock', class: 'stock-out text-danger' };
    if (stock <= 5) return { text: `Low Stock (${stock})`, class: 'stock-low text-warning' };
    return { text: `In Stock (${stock})`, class: 'stock-good text-success' };
  };

  return (
    <div className="pos-menu-grid">
      {menu.map((item) => {
        const stockStatus = getStockStatus(item.stock);
        const quantity = quantities[item.id] || 1;

        return (
          <div key={item.id} className="menu-item-card">
            <div className="menu-item-header">
              <h5 className="menu-item-name">{item.name}</h5>
              <span className="menu-item-price">
                GHS {parseFloat(item.price || 0).toFixed(2)}
              </span>
            </div>

            <div className={`menu-item-stock ${stockStatus.class}`}>
              <i className={`fas ${
                item.stock === 0 ? 'fa-times-circle' : 
                item.stock <= 5 ? 'fa-exclamation-triangle' : 
                'fa-check-circle'
              } me-1`}></i>
              {stockStatus.text}
            </div>

            <div className="menu-item-actions">
              <div className="quantity-selector">
                <button 
                  className="btn btn-outline-secondary btn-sm qty-btn"
                  onClick={() => handleQuantityChange(item.id, Math.max(1, quantity - 1))}
                  disabled={item.stock === 0 || quantity <= 1}
                >
                  <i className="fas fa-minus"></i>
                </button>
                <input
                  type="number"
                  min="1"
                  max={item.stock}
                  value={quantity}
                  onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                  className="form-control form-control-sm qty-input"
                  disabled={item.stock === 0}
                />
                <button 
                  className="btn btn-outline-secondary btn-sm qty-btn"
                  onClick={() => handleQuantityChange(item.id, Math.min(item.stock, quantity + 1))}
                  disabled={item.stock === 0 || quantity >= item.stock}
                >
                  <i className="fas fa-plus"></i>
                </button>
              </div>
              <button
                onClick={() => handleAddToCart(item)}
                disabled={item.stock === 0}
                className={`btn add-to-cart-btn ${
                  item.stock === 0 ? 'btn-secondary' : 'btn-danger'
                }`}
              >
                <i className={`fas ${
                  item.stock === 0 ? 'fa-ban' : 'fa-cart-plus'
                } me-1`}></i>
                {item.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
              </button>
            </div>
          </div>
        );
      })}

      {menu.length === 0 && (
        <div className="no-menu-items">
          <i className="fas fa-utensils fa-3x text-muted mb-3"></i>
          <p className="text-muted mb-0">No menu items available</p>
        </div>
      )}
    </div>
  );
};

export default MenuGrid;

src/components/POS.js.content:
import React, { useState, useEffect } from 'react';
import { useAuth } from '../services/AuthContext';
import { useCart } from '../services/CartContext';
import { getMenu, createOrder, updateMenuStock } from '../services/api';
import MenuGrid from './MenuGrid';
import Cart from './Cart';
import OrderModal from './OrderModal';

const POS = () => {
  const { user } = useAuth();
  const { cartItems, clearCart, getCartTotal } = useCart();
  const [menu, setMenu] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [showOrderModal, setShowOrderModal] = useState(false);
  const [recentOrders, setRecentOrders] = useState([]);
  const [activeSection, setActiveSection] = useState('pos');

  useEffect(() => {
    loadMenu();
    loadRecentOrders();
  }, []);

  const loadMenu = async () => {
    try {
      setIsLoading(true);
      const menuData = await getMenu();
      setMenu(menuData);
      setError('');
    } catch (error) {
      console.error('Error loading menu:', error);
      setError('Failed to load menu. Please refresh the page.');
    } finally {
      setIsLoading(false);
    }
  };

  const loadRecentOrders = () => {
    const stored = localStorage.getItem('shawarma_boss_recent_orders');
    if (stored) {
      try {
        setRecentOrders(JSON.parse(stored));
      } catch (error) {
        console.error('Error loading recent orders:', error);
      }
    }
  };

  const handleConfirmOrder = async () => {
    if (cartItems.length === 0) {
      alert('Cart is empty!');
      return;
    }

    const order = {
      id: 'order-' + Date.now() + '-' + Math.floor(Math.random() * 1000),
      user: user.username,
      staff: user.username,
      timestamp: new Date().toISOString(),
      items: cartItems,
      total: getCartTotal(),
      status: 'completed'
    };

    try {
      // Save order to backend
      await createOrder(order);

      // Update stock for each item
      for (const item of cartItems) {
        const menuItem = menu.find(m => m.id === item.id);
        if (menuItem && menuItem.stock >= item.quantity) {
          await updateMenuStock(item.id, menuItem.stock - item.quantity);
        }
      }

      // Update local menu state
      setMenu(prevMenu =>
        prevMenu.map(menuItem => {
          const cartItem = cartItems.find(item => item.id === menuItem.id);
          if (cartItem) {
            return {
              ...menuItem,
              stock: Math.max(0, menuItem.stock - cartItem.quantity)
            };
          }
          return menuItem;
        })
      );

      // Save to recent orders
      const newRecentOrders = [order, ...recentOrders.slice(0, 4)];
      setRecentOrders(newRecentOrders);
      localStorage.setItem('shawarma_boss_recent_orders', JSON.stringify(newRecentOrders));

      // Show order modal
      setShowOrderModal(true);

      // Clear cart
      clearCart();
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Failed to create order. Please try again.');
    }
  };

  // Sidebar navigation items for staff dashboard
  const sidebarItems = [
    { id: 'pos', icon: 'fas fa-cash-register', label: 'Point of Sale' },
    { id: 'orders', icon: 'fas fa-receipt', label: 'Recent Orders' },
    { id: 'menu', icon: 'fas fa-utensils', label: 'Menu Items' },
    { id: 'stats', icon: 'fas fa-chart-line', label: 'Quick Stats' }
  ];

  // Calculate quick stats for staff dashboard
  const calculateStats = () => {
    const todayOrders = recentOrders.filter(order => {
      const orderDate = new Date(order.timestamp).toDateString();
      const today = new Date().toDateString();
      return orderDate === today;
    });

    const todayRevenue = todayOrders.reduce((sum, order) => sum + parseFloat(order.total || 0), 0);
    const totalItems = recentOrders.reduce((sum, order) => sum + (order.items?.length || 0), 0);

    return {
      totalOrders: recentOrders.length,
      todayOrders: todayOrders.length,
      todayRevenue,
      totalItems
    };
  };

  const stats = calculateStats();

  // Render different sections based on active selection
  const renderPOSSection = () => (
    <div className="pwa-pos-grid">
      {/* Menu Section */}
      <div className="pwa-menu-section">
        <div className="pwa-section-header">
          <div className="pwa-header-content">
            <div className="pwa-header-icon">
              <i className="fas fa-utensils"></i>
            </div>
            <div className="pwa-header-text">
              <h5 className="pwa-section-title">Menu Items</h5>
              <span className="pwa-section-subtitle">{menu.length} items available</span>
            </div>
          </div>
          <div className="pwa-header-actions">
            <button
              onClick={loadMenu}
              className="pwa-refresh-btn"
              title="Refresh menu"
            >
              <i className="fas fa-sync"></i>
            </button>
          </div>
        </div>
        <div className="pwa-menu-container">
          <MenuGrid menu={menu} />
        </div>
      </div>

      {/* Cart Section */}
      <div className="pwa-cart-section">
        <div className="pwa-section-header">
          <div className="pwa-header-content">
            <div className="pwa-header-icon pwa-cart-icon">
              <i className="fas fa-shopping-cart"></i>
            </div>
            <div className="pwa-header-text">
              <h5 className="pwa-section-title">Current Order</h5>
              <span className="pwa-section-subtitle">
                {cartItems.length} {cartItems.length === 1 ? 'item' : 'items'} in cart
              </span>
            </div>
          </div>
          <div className="pwa-cart-total">
            <span className="pwa-total-label">Total</span>
            <span className="pwa-total-amount">GHS {getCartTotal().toFixed(2)}</span>
          </div>
        </div>
        <div className="pwa-cart-container">
          <Cart onConfirmOrder={handleConfirmOrder} />
        </div>
      </div>
    </div>
  );

  const renderRecentOrdersSection = () => (
    <div className="card">
      <div className="card-header bg-success text-white">
        <h5 className="mb-0">
          <i className="fas fa-clock me-2"></i>
          Recent Orders
        </h5>
      </div>
      <div className="card-body">
        {recentOrders.length === 0 ? (
          <div className="text-center text-muted py-5">
            <i className="fas fa-receipt fa-3x text-muted mb-3"></i>
            <h6>No recent orders</h6>
            <p className="mb-0">Orders will appear here as they are created</p>
          </div>
        ) : (
          <div className="row g-3">
            {recentOrders.map((order) => (
              <div key={order.id} className="col-md-6 col-lg-4">
                <div className="card border">
                  <div className="card-body">
                    <div className="d-flex justify-content-between align-items-start mb-2">
                      <h6 className="card-title mb-0">#{order.id.slice(-6)}</h6>
                      <span className="badge bg-success">Completed</span>
                    </div>
                    <p className="text-muted small mb-2">
                      {new Date(order.timestamp).toLocaleString()}
                    </p>
                    <div className="d-flex justify-content-between">
                      <span className="text-muted">{order.items?.length || 0} items</span>
                      <span className="fw-bold text-success">GHS {parseFloat(order.total || 0).toFixed(2)}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  const renderMenuSection = () => (
    <div className="card">
      <div className="card-header bg-warning text-white">
        <h5 className="mb-0">
          <i className="fas fa-utensils me-2"></i>
          Menu Items Overview
        </h5>
      </div>
      <div className="card-body">
        <div className="row g-3">
          {menu.map((item) => (
            <div key={item.id} className="col-md-6 col-lg-4">
              <div className="card border">
                <div className="card-body">
                  <h6 className="card-title">{item.name}</h6>
                  <div className="d-flex justify-content-between align-items-center">
                    <span className="text-success fw-bold">GHS {parseFloat(item.price || 0).toFixed(2)}</span>
                    <span className={`badge ${item.stock <= 0 ? 'bg-danger' : item.stock <= 5 ? 'bg-warning' : 'bg-success'}`}>
                      Stock: {item.stock}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const renderStatsSection = () => (
    <div className="space-y-6">
      <div className="row g-4 mb-4">
        <div className="col-md-3">
          <div className="bg-primary bg-opacity-10 border border-primary border-opacity-20 rounded-lg p-4 text-center">
            <div className="text-3xl font-bold text-primary mb-1">{stats.totalOrders}</div>
            <div className="text-sm text-muted">Total Orders</div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="bg-success bg-opacity-10 border border-success border-opacity-20 rounded-lg p-4 text-center">
            <div className="text-3xl font-bold text-success mb-1">{stats.todayOrders}</div>
            <div className="text-sm text-muted">Today's Orders</div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="bg-warning bg-opacity-10 border border-warning border-opacity-20 rounded-lg p-4 text-center">
            <div className="text-3xl font-bold text-warning mb-1">GHS {stats.todayRevenue.toFixed(2)}</div>
            <div className="text-sm text-muted">Today's Revenue</div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="bg-danger bg-opacity-10 border border-danger border-opacity-20 rounded-lg p-4 text-center">
            <div className="text-3xl font-bold text-danger mb-1">{stats.totalItems}</div>
            <div className="text-sm text-muted">Items Sold</div>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h5 className="mb-0">Performance Overview</h5>
        </div>
        <div className="card-body">
          <div className="text-center py-4">
            <i className="fas fa-user-check fa-3x text-success mb-3"></i>
            <h6>Logged in as: {user.username}</h6>
            <p className="text-muted mb-0">Keep up the great work!</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'orders':
        return renderRecentOrdersSection();
      case 'menu':
        return renderMenuSection();
      case 'stats':
        return renderStatsSection();
      case 'pos':
      default:
        return renderPOSSection();
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-8">
        <div className="spinner"></div>
        <span className="ml-2">Loading menu...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div className="card-body text-center">
          <div className="text-danger mb-4">{error}</div>
          <button 
            onClick={loadMenu}
            className="btn btn-danger"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="pwa-staff-layout fade-in">
      {/* PWA Navigation Tabs - Mobile First */}
      <div className="pwa-nav-tabs">
        <div className="pwa-nav-container">
          {sidebarItems.map((item) => (
            <button
              key={item.id}
              className={`pwa-nav-tab ${activeSection === item.id ? 'active' : ''}`}
              onClick={() => setActiveSection(item.id)}
            >
              <div className="pwa-tab-icon">
                <i className={item.icon}></i>
              </div>
              <span className="pwa-tab-label">{item.label}</span>
            </button>
          ))}
        </div>
      </div>

      {/* PWA Main Content */}
      <div className="pwa-main-content">
        {/* PWA Header */}
        <div className="pwa-main-header">
          <div className="pwa-header-info">
            <h2 className="pwa-main-title">
              <i className={`${sidebarItems.find(item => item.id === activeSection)?.icon || 'fas fa-dashboard'}`}></i>
              {sidebarItems.find(item => item.id === activeSection)?.label || 'Dashboard'}
            </h2>
            <p className="pwa-main-subtitle">
              {activeSection === 'pos' && 'Create orders and manage transactions'}
              {activeSection === 'orders' && 'View all recent order history'}
              {activeSection === 'menu' && 'Browse available menu items'}
              {activeSection === 'stats' && 'View performance statistics'}
            </p>
          </div>
          <div className="pwa-header-user">
            <div className="pwa-user-avatar">
              <i className="fas fa-user"></i>
            </div>
            <div className="pwa-user-info">
              <span className="pwa-user-name">{user.username}</span>
              <span className="pwa-user-role">Staff Member</span>
            </div>
          </div>
        </div>

        {/* Dynamic Content Area */}
        <div className="pwa-content-area">
          {renderContent()}
        </div>
      </div>

      {/* Order Success Modal */}
      {showOrderModal && (
        <OrderModal
          order={recentOrders[0]}
          onClose={() => setShowOrderModal(false)}
        />
      )}
    </div>
  );
};

export default POS;

